import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { getContact } from '../api/ContactService'

export function ContactDetail({ updateContact, updateImage }) {
	const [contact, setContact] = useState({
		name: '',
		email: '',
		phone: '',
		address: '',
		title: '',
		status: '',
		photoUrl: '',
	})

	const { id } = useParams()

	const fetchContact = async (contact) => {
		try {
			const { data } = await getContact(id)
			setContact(data)
		} catch (error) {
			console.log(error)
		}
	}

    useEffect(() => {
        fetchContact()
    })
	return <div>ContactDetail</div>
}
